import * as React from 'react';

export interface Props {
  /**
   * Makes menu list item highlighted (active)
   *
   * @default false
   */
  active?: boolean;
  /**
   * Menu list item link's `href` attribute
   */
  href?: string | boolean;
  /**
   * Content of the media area (e.g. icon)
   */
  media?: React.ReactNode;
  /**
   * Content of the menu list item "subtitle" area
   */
  subtitle?: string | number | React.ReactNode;
}


// @ts-ignore
interface MenuListItemProps extends Omit<React.ComponentProps<'div'>, keyof Props>, Props {
  ref?: React.Ref<HTMLElement>;
}

declare const MenuListItem: React.ComponentType<MenuListItemProps>;

export default MenuListItem;