import * as React from 'react';

export interface Props {
  /**
   * Component's HTML Element
   *
   * @default 'a'
   */
  component?: string;
  /**
   * Text content of the back link
   *
   * @default 'Back'
   */
  text?: string | React.ReactNode;
  /**
   * Defines whether to show the link text. When 'auto', it hides link text for Material theme
   *
   * @default false
   */
  showText?: boolean;
  /**
   * Link click handler
   */
  onClick?: (e: any) => void;
}


// @ts-ignore
interface NavbarBackLinkProps extends Omit<React.ComponentProps<'a'>, keyof Props>, Props {
  ref?: React.Ref<HTMLElement>;
}

declare const NavbarBackLink: React.ComponentType<NavbarBackLinkProps>;

export default NavbarBackLink;