import * as React from 'react';

export interface Props {
  /**
   * Component's HTML Element
   *
   * @default 'button'
   */
  component?: string;
  /**
   * Highlights button as active
   *
   * @default false
   */
  active?: boolean;
}


// @ts-ignore
interface SegmentedButtonProps extends Omit<React.ComponentProps<'button'>, keyof Props>, Props {
  ref?: React.Ref<HTMLElement>;
}

declare const SegmentedButton: React.ComponentType<SegmentedButtonProps>;

export default SegmentedButton;