import * as React from 'react';

export interface Props {
  /**
   * Enables Tabbar with labels
   *
   * @default false
   */
  labels?: boolean;

  /**
   * Enables Tabbar with icons
   *
   * @default false
   */
  icons?: boolean;
}


// @ts-ignore
interface TabbarProps extends Omit<React.ComponentProps<'div'>, keyof Props>, Props {
  ref?: React.Ref<HTMLElement>;
}

declare const Tabbar: React.ComponentType<TabbarProps>;

export default Tabbar;