import { SvelteComponent, Snippet } from 'svelte';
import { HTMLAttributes } from 'svelte/elements';

export interface Props {
  class?: string;
  /**
   * Component's HTML Element
   *
   * @default 'i'
   */
  component?: string;

  /**
   * Icon badge
   * */
  badge?: Snippet | string;

  /**
   * Icon to render in "ios" theme
   * */
  ios?: Snippet | string;

  /**
   * Icon to render in "material" theme
   * */
  material?: Snippet | string;

  /**
   * Badge colors. Object with Tailwind CSS colors classes
   * */
  badgeColors?: {
    /**
     * Badge bg color
     *
     * @default 'bg-primary'
     */
    bg?: string;
    /**
     * Badge text color
     *
     * @default 'text-white'
     */
    text?: string;
  };
}


interface IconProps {}
interface IconProps extends Props {}
interface IconEvents extends Record<'',{}>{}

declare class Icon extends SvelteComponent<
  IconProps & HTMLAttributes<HTMLElement>,
  IconEvents,
  {
    
  }
> {}

export default Icon;