        /**
         *
         * @default ''
         */
        navbarTextIos?: string;
        /**
         *
         * @default ''
         */
        navbarTextMaterial?: string;
        /**
         *
         * @default ''
         */
        toolbarTextIos?: string;
        /**
         *
         * @default 'text-md-light-primary dark:text-md-dark-primary'
         */
